CREATE OR REPLACE FUNCTION public.register_integration_suricata(srv_id integer)RETURNS void LANGUAGE plpgsql AS $function$ declare grp_id integer; mod_id bigint;
begin INSERT INTO utm_module (pretty_name, module_description, module_active, module_icon, module_name, server_id, module_category, needs_restart, lite_version, is_activatable) VALUES ('Suricata', 'Suricata is an open-source based intrusion detection system (IDS) and intrusion prevention system (IPS).', FALSE, 'suricata.png', 'SURICATA', srv_id, 'Device', FALSE, TRUE, TRUE) ON CONFLICT (module_name, server_id) DO UPDATE SET pretty_name = 'Suricata', module_icon = 'suricata.png', module_name = 'SURICATA', module_category = 'Device', module_description = 'Suricata is an open-source based intrusion detection system (IDS) and intrusion prevention system (IPS).', lite_version = TRUE, server_id = srv_id;
end; $function$;


