CREATE OR REPLACE FUNCTION public.register_integration_utmstack(srv_id integer)RETURNS void LANGUAGE plpgsql AS $function$ declare grp_id integer; mod_id bigint;
begin INSERT INTO utm_module (pretty_name, module_description, module_active, module_icon, module_name, server_id, module_category, needs_restart, lite_version, is_activatable) VALUES ('Utmstack', 'UTMStack is an open‑source SIEM and XDR platform that centralizes telemetry, real‑time detection, and automated response.', FALSE, 'utmstack.png', 'UTMSTACK', srv_id, 'Device', FALSE, TRUE, TRUE) ON CONFLICT (module_name, server_id) DO UPDATE SET pretty_name = 'Utmstack', module_icon = 'utmstack.png', module_name = 'UTMSTACK', module_category = 'Device', module_description = 'UTMStack is an open‑source SIEM and XDR platform that centralizes telemetry, real‑time detection, and automated response.', lite_version = TRUE, server_id = srv_id;
end; $function$;


